'use strict';

/*
 * @api public
 * @property {function} format
 * Both the construction method and set of exposed
 * formats.
 */
const format = exports.format = import('././format');

/*
 * @api public
 * @method {function} levels
 * Registers the specified levels with logform.
 */
exports.levels = import('././levels');

//
// Setup all transports as eager-loaded exports
// so that they are static for the bundlers.
//
Object.defineProperty(format, 'align',       { value: import('./align') });
Object.defineProperty(format, 'cli',         { value: import('./cli') });
Object.defineProperty(format, 'colorize',    { value: import('./colorize') });
Object.defineProperty(format, 'combine',     { value: import('./combine') });
Object.defineProperty(format, 'errors',      { value: import('./errors') });
Object.defineProperty(format, 'json',        { value: import('./json') });
Object.defineProperty(format, 'label',       { value: import('./label') });
Object.defineProperty(format, 'logstash',    { value: import('./logstash') });
Object.defineProperty(format, 'metadata',    { value: import('./metadata') });
Object.defineProperty(format, 'ms',          { value: import('./ms') });
Object.defineProperty(format, 'padLevels',   { value: import('./pad-levels') });
Object.defineProperty(format, 'prettyPrint', { value: import('./pretty-print') });
Object.defineProperty(format, 'printf',      { value: import('./printf') });
Object.defineProperty(format, 'simple',      { value: import('./simple') });
Object.defineProperty(format, 'splat',       { value: import('./splat') });
Object.defineProperty(format, 'timestamp',   { value: import('./timestamp') });
Object.defineProperty(format, 'uncolorize',  { value: import('./uncolorize') });
